WorkflowProcessEvent.eventDescription:
function afterTaskComplete(colleagueId,nextSequenceId,userList){
	
	if(nextSequenceId == REPRESENTANTE
			|| nextSequenceId == GESTOR_TERRITORIAL
			|| nextSequenceId == GESTOR_COMERCIAL){
		
		 hAPI.setCardValue("encaminharAtividade", "");
	
	}
	
	
}

var INICIO_0 = 0;
var INICIO = 4;
var REPRESENTANTE = 5;
var GESTOR_TERRITORIAL = 11;
var GESTOR_COMERCIAL = 13;
var FIM = 24;

function beforeTaskSave(colleagueId,nextSequenceId,userList){
	
	if(nextSequenceId == INICIO
		|| nextSequenceId == REPRESENTANTE
		|| nextSequenceId == GESTOR_TERRITORIAL
		|| nextSequenceId == GESTOR_COMERCIAL
		|| nextSequenceId == FIM
		 ){
		var fields = ["colleagueName"];
		var c1 = DatasetFactory.createConstraint("colleaguePK.colleagueId", getValue("WKUser"), getValue("WKUser"), ConstraintType.MUST);
		var c2 = DatasetFactory.createConstraint("colleaguePK.companyId", getValue("WKCompany"), getValue("WKCompany"), ConstraintType.MUST);
		var datasetColleague = DatasetFactory.getDataset("colleague", fields, [ c1, c2 ], null);
		var nomeUserInteracao = '';
		if(dsTemValor(datasetColleague)){
			nomeUserInteracao = datasetColleague.getValue(0, "colleagueName");
		}
		
		var childData = new java.util.HashMap();
		childData.put("intWKUserUsuario", getValue("WKUser") );
		childData.put("intNomeUsuario", nomeUserInteracao );
		childData.put("intDataHoraInteracao", dataAtual("dd/mm/yyyy hh:mm") );
		childData.put("intObservacao", hAPI.getCardValue("obsInteracao"));
		
		hAPI.addCardChild("tbInteracoes", childData);
		
		
		hAPI.setCardValue("obsInteracao", "");
	}
	
	
}

function dataHoraAtual(){
    var data = new Date();
    
    var dia = addZero(data.getDate());
    var mes = addZero(data.getMonth()+1);
    var ano = data.getFullYear(); 
    
    var hora = addZero(data.getHours());
    var minuto = addZero(data.getMinutes());
    var segundo = addZero(data.getSeconds());
    
    return dia + "/" + mes + "/" + ano + " " + hora + ":" + minuto + ":" + segundo;
}

function dataAtual(formato){
    var retornoData = "";
	var data = new Date();
    
    var dia = addZero(data.getDate());
    var mes = addZero(data.getMonth()+1);
    var ano = data.getFullYear(); 
    
    var hora = addZero(data.getHours());
    var minuto = addZero(data.getMinutes());
    
    if(formato == "dd/mm/yyyy"){
    	retornoData = dia + "/" + mes + "/" + ano;
    }else if(formato == "yyyymmdd"){
    	retornoData = ano + "" + mes + "" + dia;
    }else if(formato == "yyyy-mm-dd"){
    	retornoData = ano + "-" + mes + "-" + dia;
    }else if(formato == "dd/mm/yyyy hh:mm"){
    	retornoData = dia + "/" + mes + "/" + ano + " " + hora + ":" +minuto;
    }
    
    return retornoData;
}

function addZero(i) {
    if (i < 10) {
        i = "0" + i;
    }
    return i;
}

function temValor(valor){
	if(valor != null && valor != undefined){
		valor = valor.toString().trim();
		
		if(valor != ""){
			return true;
		}else{
			return false;
		}
	}
	
	return false;
}

function temValorArray(arrayValor){
	if(arrayValor != null && arrayValor != undefined && arrayValor.length > 0){
		return true;
	}else{
		return false;
	}
}

function dsTemValor(dataset){
	if(dataset != null && dataset != undefined && dataset.rowsCount > 0){
		return true;
	}else{
		return false;
	}
}

function buscarData(){
	return new Date();
}

function buscarDiaAtual(){
	return buscarData().getDate().toString();
}

function buscarMesAtual(){
	return (buscarData().getMonth()+1).toString();
}

function buscarAnoAtual(){
	return buscarData().getFullYear().toString();
}

function buscarDataAtualSistema(){
	return formatarData(buscarDiaAtual(), buscarMesAtual(), buscarAnoAtual());
}

function formatarData(dia, mes, ano){
	if(dia.length == 1)
		dia = 0+dia;	
	if(mes.length == 1)
		mes = 0+mes;	
	return dia+"/"+mes+"/"+ano;
}

function buscarIdSolicitacao(){
	return getValue("WKNumProces");
}

function buscarUsuarioLogado(){
	return getValue("WKUser");
}

function buscarEmpresa(){
	return getValue("WKCompany");
}

/*
 * Retorna o Valor do Campo
 */
function buscarValorCampo(nomeCampo) {
	return hAPI.getCardValue(nomeCampo);
}

/*
 * Método para retornar os Dados do dataSet selecionado
 */
function getValorDataSet(nomeDataSet, campoIdDataSet, valorIdDataSet, nomeCampo) {
	var dataSet = DatasetFactory.getDataset(nomeDataSet, null, null, null);
	var valorCampo = false;

	for ( var i = 0; i < dataSet.rowsCount; i++) {
		if (dataSet.getValue(i, codigoDataSet) == valorIdDataSet) {
			valorCampo = dataSet.getValue(i, nomeCampo);
		}
	}
	return valorCampo;
}

